sys_script_include.script:
var MyBravTEST = Class.create();
MyBravTEST.prototype = {
    initialize: function() {},

    type: 'MyBravTEST'
};

function DEVTOOLTest() {
    var installer = new x_snc_devtools.InstallerAPI(x_snc_devtools.GetAppName('x_snc_devtools'));
    gs.info("ins" + installer);

    var grScriptInclude = new GlideRecord('sys_script_include');
    gs.info("grScriptInclude" + grScriptInclude);
    grScriptInclude.addQuery('api_name', 'global.TemplateHandler');
    grScriptInclude.query();

    if (grScriptInclude.next()) {
        gs.info("next" + grScriptInclude.next());
        // x_snc_devtools.GlideRecordDelete(grScriptInclude);
        installer.RecordDelete('Remove Script Include with api-name global.TemplateHandler', 'sys_script_include', grScriptInclude.sys_id);
    }
}
DEVTOOLTest();
